commits.content:
/* Keep legacy import as a fallback for older tooling (commented). */
/* @import "tailwindcss"; */

:root {
  /* GitHub-like dark theme tokens */
  --bg: #0d1117;           /* page background */
  --panel: #0f1419;        /* panel / surface */
  --panel-2: #0b1216;      /* darker panel for sidebars */
  --border: rgba(255,255,255,0.04);
  --muted: #8b949e;        /* secondary text */
  --fg: #c9d1d9;           /* primary foreground */
  --accent: #58a6ff;       /* primary accent */
  --accent-600: #2386ff;   /* stronger accent */
  --radius: 8px;
  --glass: rgba(255,255,255,0.02);
}

[data-theme="light"] {
  --bg: #f6f8fa; /* light surface */
  --panel: #ffffff; /* white panels */
  --muted: #4a5568; /* gray-700 */
  --fg: #0b0d0f; /* dark text */
  --accent: #2563eb; /* blue-600 */
}

* { box-sizing: border-box; }

html, body, #__next {
  height: 100%;
}

body {
  margin: 0;
  background: var(--bg);
  color: var(--fg);
  overflow-x: hidden;
  font-family: Inter, ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto,
    "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji",
    "Segoe UI Symbol";
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  line-height: 1.45;
  touch-action: manipulation;
}

/* layout helpers */
.container {
  max-width: 100%;
  margin: 16px auto;
  padding: 0 16px;
}

.page {
  display: flex;
  gap: 24px;
}

.panel {
  background: linear-gradient(180deg, rgba(255,255,255,0.01), transparent), var(--panel);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 18px;
  box-shadow: 0 8px 20px rgba(2,6,23,0.55);
}

.card {
  background: linear-gradient(180deg, rgba(255,255,255,0.01), transparent), var(--panel-2);
  padding: 18px;
  border-radius: 10px;
  border: 1px solid rgba(255,255,255,0.02);
}

/* Headline sizing */
h1 { font-size: 34px; margin: 0 0 12px 0; }

/* form elements */
input, textarea, select {
  width: 100%;
  padding: 10px 12px;
  border-radius: 8px;
  background: rgba(255,255,255,0.02);
  color: var(--fg);
  border: 1px solid var(--border);
  outline: none;
  transition: box-shadow .12s ease, border-color .12s ease;
}

input:focus, textarea:focus, select:focus {
  box-shadow: 0 4px 14px rgba(83,155,245,0.09);
  border-color: rgba(83,155,245,0.7);
}

label { color: var(--muted); font-size: 13px; display: block; margin-bottom: 8px; }

.muted { color: var(--muted); }

/* Buttons */
.btn {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  border-radius: 8px;
  border: 1px solid transparent;
  cursor: pointer;
  font-weight: 600;
}

.btn-primary {
  background: linear-gradient(180deg, #1f6feb, #1a5fc9);
  color: white;
  box-shadow: 0 1px 0 rgba(27,31,36,0.04), inset 0 1px 0 rgba(255,255,255,0.25);
  transition: all 0.15s ease;
}

.btn-primary:hover {
  background: linear-gradient(180deg, #1a66e8, #1558c0);
  box-shadow: 0 2px 4px rgba(31,111,235,0.2);
}

.btn-ghost {
  background: transparent;
  border: 1px solid var(--border);
  color: var(--fg);
}

.link { color: var(--accent); text-decoration: none; }

/* GitHub-style Header */
.github-header {
  position: sticky;
  top: 0;
  z-index: 100;
  background: rgba(13, 17, 23, 0.95);
  backdrop-filter: blur(12px);
  border-bottom: 1px solid var(--border);
  height: 54px;
}

.github-header-inner {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 100%;
  padding: 0 16px;
  max-width: 100%;
}

.github-header-left {
  display: flex;
  align-items: center;
  gap: 16px;
}

.github-menu-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  color: var(--fg);
  cursor: pointer;
  border-radius: 6px;
  transition: background 0.15s ease;
}

.github-menu-btn:hover {
  background: rgba(255,255,255,0.05);
}

.github-icon {
  width: 16px;
  height: 16px;
}

.github-brand {
  display: flex;
  align-items: center;
  gap: 10px;
  text-decoration: none;
}

.github-logo-icon {
  width: 32px !important;
  height: 32px !important;
  min-width: 32px !important;
  max-width: 32px !important;
  min-height: 32px !important;
  max-height: 32px !important;
  background: linear-gradient(135deg, #3b82f6, #06b6d4);
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  flex-shrink: 0;
  overflow: hidden;
}

.github-logo-svg {
  width: 20px !important;
  height: 20px !important;
  min-width: 20px !important;
  max-width: 20px !important;
  min-height: 20px !important;
  max-height: 20px !important;
  flex-shrink: 0;
}

.github-brand-text {
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.github-brand-name {
  font-size: 15px;
  font-weight: 600;
  color: var(--fg);
  line-height: 1;
}

.github-brand-subtitle {
  font-size: 11px;
  color: var(--muted);
  line-height: 1;
}

.github-header-right {
  display: flex;
  align-items: center;
  gap: 12px;
}

.github-header-actions {
  display: flex;
  align-items: center;
  gap: 8px;
}

.github-btn {
  display: inline-flex;
  align-items: center;
  padding: 6px 14px;
  font-size: 14px;
  font-weight: 500;
  color: var(--fg);
  background: transparent;
  border: 1px solid var(--border);
  border-radius: 6px;
  text-decoration: none;
  transition: all 0.15s ease;
}

.github-btn:hover {
  background: rgba(255,255,255,0.05);
  border-color: rgba(255,255,255,0.1);
}

.github-btn-primary {
  display: inline-flex;
  align-items: center;
  padding: 6px 14px;
  font-size: 14px;
  font-weight: 500;
  color: #fff;
  background: linear-gradient(180deg, #1f6feb, #1a5fc9);
  border: 1px solid rgba(240,246,252,0.1);
  border-radius: 6px;
  text-decoration: none;
  transition: all 0.15s ease;
  box-shadow: 0 1px 0 rgba(27,31,36,0.04), inset 0 1px 0 rgba(255,255,255,0.25);
}

.github-btn-primary:hover {
  background: linear-gradient(180deg, #1a66e8, #1558c0);
  box-shadow: 0 2px 4px rgba(31,111,235,0.2);
}

.github-user-menu {
  position: relative;
}

.github-user-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 4px 8px 4px 4px;
  background: transparent;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: background 0.15s ease;
}

.github-user-btn:hover {
  background: rgba(255,255,255,0.05);
}

.github-avatar {
  width: 32px;
  height: 32px;
  border-radius: 50%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #3b82f6, #06b6d4);
  color: white;
  font-weight: 600;
  font-size: 14px;
}

.github-avatar-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.github-avatar-fallback {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.github-username {
  font-size: 14px;
  color: var(--fg);
  font-weight: 500;
}

.github-dropdown-arrow {
  width: 12px;
  height: 12px;
  color: var(--muted);
  transition: transform 0.15s ease;
}

.github-dropdown {
  position: absolute;
  top: calc(100% + 8px);
  right: 0;
  min-width: 200px;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(2,6,23,0.7);
  z-index: 200;
  padding: 8px;
}

.github-dropdown-header {
  padding: 8px 12px;
  border-bottom: 1px solid var(--border);
  margin: -8px -8px 8px -8px;
}

.github-dropdown-user-info {
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.github-dropdown-name {
  font-size: 14px;
  font-weight: 600;
  color: var(--fg);
}

.github-dropdown-email {
  font-size: 12px;
  color: var(--muted);
}

.github-dropdown-divider {
  height: 1px;
  background: var(--border);
  margin: 8px 0;
}

.github-dropdown-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 8px 12px;
  font-size: 14px;
  color: var(--fg);
  text-decoration: none;
  border-radius: 6px;
  border: none;
  background: transparent;
  width: 100%;
  text-align: left;
  cursor: pointer;
  transition: background 0.15s ease;
}

.github-dropdown-item:hover {
  background: rgba(255,255,255,0.05);
}

.github-dropdown-icon {
  width: 16px;
  height: 16px;
  color: var(--muted);
}

/* GitHub-style Drawer - Always slides from left, closed by default */
.github-sidebar {
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  width: 280px;
  max-width: 80vw;
  background: var(--panel-2);
  border-right: 1px solid var(--border);
  transform: translateX(-100%);
  transition: transform 0.25s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: 100;
  overflow-y: auto;
  padding: 0 16px 16px;
  display: block;
  box-shadow: 2px 0 8px rgba(0, 0, 0, 0.1);
  /* Ensure it stays on the left */
  right: auto;
}

/* Drawer opens on toggle (mobile and desktop) */
body[data-drawer='open'] .github-sidebar {
  transform: translateX(0);
}

.github-backdrop {
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.6);
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.25s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: 90;
  display: none;
}

body[data-drawer='open'] .github-backdrop {
  opacity: 1;
  pointer-events: auto;
  display: block;
}

/* Desktop: No backdrop, but drawer still toggleable */
@media (min-width: 1024px) {
  .github-sidebar {
    box-shadow: 2px 0 12px rgba(0, 0, 0, 0.15);
  }
  
  /* No backdrop on desktop - can interact with content behind */
  .github-backdrop {
    display: none !important;
  }
  
  /* Push content when drawer is open on desktop */
  body[data-drawer='open'] .dashboard-main {
    margin-left: 280px !important;
  }
}

/* Drawer Header */
.drawer-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 16px 16px;
  border-bottom: 1px solid var(--border);
  margin: 0 -16px 12px -16px;
  background: var(--panel-2);
}

.drawer-brand {
  display: flex;
  align-items: center;
  gap: 10px;
}

.drawer-logo {
  width: 32px;
  height: 32px;
  background: linear-gradient(135deg, #3b82f6, #06b6d4);
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
}

.drawer-logo-svg {
  width: 20px;
  height: 20px;
}

.drawer-brand-name {
  font-size: 16px;
  font-weight: 700;
  color: var(--fg);
}

.drawer-close-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  color: var(--muted);
  cursor: pointer;
  border-radius: 6px;
  transition: all 0.15s ease;
}

.drawer-close-btn:hover {
  background: rgba(255, 255, 255, 0.05);
  color: var(--fg);
}

.github-sidebar-header {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 20px;
  padding-bottom: 16px;
  border-bottom: 1px solid var(--border);
}

.github-sidebar-logo {
  width: 32px;
  height: 32px;
  background: linear-gradient(135deg, #3b82f6, #06b6d4);
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
}

.github-sidebar-logo-svg {
  width: 20px;
  height: 20px;
}

.github-sidebar-brand {
  font-size: 16px;
  font-weight: 700;
  color: var(--fg);
}

.github-sidebar-nav {
  display: flex;
  flex-direction: column;
  gap: 2px;
  margin-top: 8px;
}

.github-sidebar-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 8px 12px;
  font-size: 14px;
  color: var(--fg);
  text-decoration: none;
  border-radius: 6px;
  transition: background 0.15s ease;
}

.github-sidebar-item:hover {
  background: rgba(255,255,255,0.05);
}

.github-sidebar-item.active {
  background: rgba(88, 166, 255, 0.15);
  font-weight: 600;
}

.github-sidebar-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted);
}

.github-sidebar-item.active .github-sidebar-icon {
  color: var(--accent);
}

.github-sidebar-label {
  flex: 1;
}

/* Calendar Styles */
.calendar-container {
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
}

.calendar-header {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.calendar-title {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 700;
}

.calendar-controls {
  display: flex;
  align-items: center;
  gap: 8px;
  flex-wrap: wrap;
  width: 100%;
}

.calendar-controls .btn-group {
  margin-right: 4px;
}

.calendar-controls .btn {
  padding: 8px 14px;
  font-size: 0.875rem;
}

.calendar-nav {
  display: flex;
  align-items: center;
  gap: 6px;
  flex: 1;
  min-width: 0;
}

.calendar-nav-btn {
  padding: 8px 12px !important;
  min-width: auto !important;
  font-size: 0.875rem !important;
}

.calendar-date-input {
  flex: 1;
  max-width: 180px;
  font-size: 0.875rem;
  padding: 8px;
  text-align: center;
}

.calendar-today-btn {
  padding: 8px 16px;
  font-size: 0.875rem;
  white-space: nowrap;
}

.calendar-description {
  margin-top: 8px;
  margin-bottom: 12px;
  font-size: 0.875rem;
}

/* Mobile Calendar Styles */
@media (max-width: 640px) {
  .calendar-header {
    gap: 12px;
  }
  
  .calendar-title {
    font-size: 1.25rem;
  }
  
  .calendar-controls {
    flex-direction: column;
    align-items: stretch;
    gap: 12px;
  }
  
  .calendar-controls .btn-group {
    width: 100%;
    margin-right: 0;
  }
  
  .calendar-controls .btn-group .btn {
    flex: 1;
    padding: 10px 16px;
    font-size: 1rem;
  }
  
  .calendar-nav {
    width: 100%;
    flex: unset;
  }
  
  .calendar-nav-btn {
    padding: 10px 14px !important;
    font-size: 1rem !important;
  }
  
  .calendar-date-input {
    flex: 1;
    max-width: none;
    font-size: 1rem;
    padding: 10px 12px;
  }
  
  .calendar-today-btn {
    width: 100%;
    padding: 10px 16px;
    font-size: 1rem;
  }
  
  .calendar-description {
    font-size: 0.8125rem;
  }
}

/* small utilities */
.row { display:flex; gap:12px; align-items:center; }
.small { font-size:13px; }

/* GitHub-style Dashboard */
.github-container {
  max-width: 1280px;
  margin: 0 auto;
  padding: 24px 16px;
}

.github-page-header {
  margin-bottom: 32px;
}

.github-page-title {
  font-size: 28px;
  font-weight: 600;
  color: var(--fg);
  margin: 0 0 8px 0;
  line-height: 1.25;
}

.github-page-description {
  font-size: 16px;
  color: var(--muted);
  margin: 0;
}

.github-dashboard-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
  gap: 16px;
}

.github-card {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 20px;
  transition: all 0.15s ease;
}

.github-card-interactive {
  display: flex;
  align-items: flex-start;
  gap: 16px;
  text-decoration: none;
  cursor: pointer;
}

.github-card-interactive:hover {
  background: rgba(88, 166, 255, 0.03);
  border-color: rgba(88, 166, 255, 0.2);
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(2,6,23,0.4);
}

.github-card-icon {
  width: 48px;
  height: 48px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

.github-card-icon-home {
  background: linear-gradient(135deg, #3b82f6, #2563eb);
  color: white;
}

.github-card-icon-calendar {
  background: linear-gradient(135deg, #8b5cf6, #7c3aed);
  color: white;
}

.github-card-icon-learn {
  background: linear-gradient(135deg, #10b981, #059669);
  color: white;
}

.github-card-icon-assistant {
  background: linear-gradient(135deg, #f59e0b, #d97706);
  color: white;
}

.github-card-icon-profile {
  background: linear-gradient(135deg, #ec4899, #db2777);
  color: white;
}

.github-card-content {
  flex: 1;
}

.github-card-title {
  font-size: 16px;
  font-weight: 600;
  color: var(--fg);
  margin: 0 0 6px 0;
}

.github-card-description {
  font-size: 14px;
  color: var(--muted);
  margin: 0;
  line-height: 1.5;
}

.github-card-arrow {
  font-size: 20px;
  color: var(--muted);
  opacity: 0;
  transform: translateX(-8px);
  transition: all 0.15s ease;
}

.github-card-interactive:hover .github-card-arrow {
  opacity: 1;
  transform: translateX(0);
}

.github-loading {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  gap: 16px;
  color: var(--muted);
}

.github-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid var(--border);
  border-top-color: var(--accent);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes pulse {
  0%, 100% { opacity: 0.6; }
  50% { opacity: 1; }
}

@keyframes premium-glow {
  0% {
    background-position: 0% 50%;
    box-shadow: 0 0 20px rgba(234, 179, 8, 0.3);
  }
  50% {
    background-position: 100% 50%;
    box-shadow: 0 0 30px rgba(234, 179, 8, 0.5);
  }
  100% {
    background-position: 0% 50%;
    box-shadow: 0 0 20px rgba(234, 179, 8, 0.3);
  }
}

/* responsive */
@media (max-width: 720px) {
  .container { margin: 18px 16px; }
  .page { flex-direction: column; }
  .panel { padding: 14px; }
  .dashboard-main, main, .page, .container { padding-bottom: 84px; }
  .github-dashboard-grid {
    grid-template-columns: 1fr;
  }
}

/* Dashboard main */
.dashboard-main { padding: 0; min-height: calc(100vh - 54px); }

/* GitHub-style Chat Interface */
.github-chat-panel {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  overflow: hidden;
}

.github-chat-messages {
  height: 500px;
  overflow-y: auto;
  padding: 20px;
  background: var(--panel-2);
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.github-chat-message {
  display: flex;
  flex-direction: column;
  gap: 8px;
  max-width: 85%;
}

.github-chat-user {
  align-self: flex-end;
}

.github-chat-assistant {
  align-self: flex-start;
}

.github-chat-role {
  font-size: 12px;
  font-weight: 600;
  color: var(--muted);
  display: flex;
  align-items: center;
  gap: 6px;
}

.github-chat-content {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 12px 16px;
  white-space: pre-wrap;
  word-wrap: break-word;
  font-size: 14px;
  line-height: 1.6;
}

.github-chat-user .github-chat-content {
  background: rgba(31, 111, 235, 0.15);
  border-color: rgba(31, 111, 235, 0.3);
}

.github-chat-input-area {
  padding: 16px;
  background: var(--panel);
  border-top: 1px solid var(--border);
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.github-chat-input {
  width: 100%;
  padding: 12px 16px;
  border-radius: 6px;
  background: var(--panel-2);
  color: var(--fg);
  border: 1px solid var(--border);
  outline: none;
  font-size: 14px;
  transition: all 0.15s ease;
  resize: none;
}

.github-chat-input:focus {
  border-color: rgba(31, 111, 235, 0.5);
  box-shadow: 0 0 0 3px rgba(31, 111, 235, 0.1);
}

.github-chat-actions {
  display: flex;
  gap: 8px;
  align-items: center;
  flex-wrap: wrap;
}

.github-chat-actions button {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  white-space: nowrap;
}

.github-chat-attachments {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
}

.github-attachment-tag {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 4px 10px;
  background: rgba(88, 166, 255, 0.1);
  border: 1px solid rgba(88, 166, 255, 0.3);
  border-radius: 6px;
  font-size: 12px;
  color: var(--accent);
}

.github-chat-hint {
  font-size: 12px;
  color: var(--muted);
  display: flex;
  align-items: center;
  gap: 6px;
}

kbd {
  background: var(--panel-2);
  border: 1px solid var(--border);
  border-radius: 4px;
  padding: 2px 6px;
  font-size: 11px;
  font-family: monospace;
  color: var(--fg);
}

.github-spinner-sm {
  width: 14px;
  height: 14px;
  border: 2px solid rgba(255,255,255,0.3);
  border-top-color: white;
  border-radius: 50%;
  animation: spin 0.6s linear infinite;
}

@media (max-width: 720px) {
  .github-chat-actions {
    flex-direction: column;
    align-items: stretch;
  }
  
  .github-chat-actions button {
    width: 100%;
    justify-content: center;
  }
  
  .github-chat-message {
    max-width: 95%;
  }
}

/* keep some legacy classes from earlier dev to avoid visual regressions */
a { color: var(--accent); }


/* --- Auth Pages (Login, Signup, Forgot Password) --- */
.auth-page {
  min-height: calc(100vh - 60px); /* Account for header */
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--bg);
  padding: 40px 20px;
  /* Override parent container/main styles */
  margin: -16px -16px 0 -16px !important;
  width: calc(100% + 32px) !important;
  box-sizing: border-box;
}

@media (max-width: 720px) {
  .auth-page {
    min-height: calc(100vh - 60px);
    padding: 20px 16px 40px;
  }
}

.auth-container {
  width: 100%;
  max-width: 420px;
  margin: 0 auto;
}

.auth-card {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 40px 32px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
}

.auth-header {
  text-align: center;
  margin-bottom: 32px;
}

.auth-logo {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 64px;
  height: 64px;
  margin: 0 auto 20px;
  background: linear-gradient(135deg, #3b82f6, #06b6d4);
  border-radius: 12px;
  color: white;
}

.auth-title {
  font-size: 28px;
  font-weight: 700;
  color: var(--fg);
  margin: 0 0 8px 0;
}

.auth-subtitle {
  font-size: 14px;
  color: var(--muted);
  margin: 0;
}

.auth-form {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.auth-error {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px;
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid rgba(239, 68, 68, 0.3);
  border-radius: 8px;
  color: #ef4444;
  font-size: 14px;
}

.auth-success {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px;
  background: rgba(16, 185, 129, 0.1);
  border: 1px solid rgba(16, 185, 129, 0.3);
  border-radius: 8px;
  color: #10b981;
  font-size: 14px;
}

.auth-submit {
  width: 100%;
  padding: 12px;
  font-size: 15px;
  font-weight: 600;
}

.auth-links {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  font-size: 14px;
}

.auth-link {
  color: var(--muted);
  text-decoration: none;
  transition: color 0.2s;
}

.auth-link:hover {
  color: var(--fg);
}

.auth-link-primary {
  color: var(--accent);
  font-weight: 600;
}

.auth-link-primary:hover {
  color: var(--accent);
  opacity: 0.8;
}

.auth-divider {
  color: var(--border);
}

.auth-footer {
  margin-top: 32px;
  text-align: center;
  font-size: 13px;
  color: var(--muted);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
}

.auth-footer-link {
  color: var(--muted);
  text-decoration: none;
}

.auth-footer-link:hover {
  color: var(--fg);
}

@media (max-width: 480px) {
  .auth-card {
    padding: 32px 24px;
  }
  
  .auth-title {
    font-size: 24px;
  }
}

/* Mobile Drawer Fixes */
@media (max-width: 1023px) {
  .github-sidebar {
    max-width: 280px;
    width: 280px;
  }
  
  .github-sidebar-nav {
    margin-top: 0;
    padding: 8px 0;
  }
  
  .github-sidebar-item {
    padding: 12px 16px;
    font-size: 15px;
    display: flex;
    align-items: center;
    gap: 12px;
  }
  
  .github-sidebar-icon {
    flex-shrink: 0;
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  
  .github-sidebar-icon svg {
    width: 20px;
    height: 20px;
  }
  
  .github-sidebar-label {
    flex: 1;
  }
  
  .github-backdrop {
    display: block;
  }
  
  /* Ensure drawer doesn't have weird spacing on mobile */
  body[data-drawer='open'] .dashboard-main {
    margin-left: 0 !important;
  }
}

/* Profile page responsive layout */
.profile-layout-grid {
  display: grid;
  grid-template-columns: 100%;
  gap: 20px;
  align-items: start;
  max-width: 1400px;
}

@media (min-width: 769px) {
  .profile-layout-grid {
    grid-template-columns: 550px 1fr;
    gap: 32px;
  }
}

/* --- Header / avatar styles (GitHub-like) --- */
.avatar-wrapper { width:36px; height:36px; border-radius:999px; overflow:hidden; display:inline-flex; align-items:center; justify-content:center; background: linear-gradient(135deg,#3b82f6,#06b6d4); color:white; font-weight:700; }
.avatar-img { display:block; width:36px; height:36px; object-fit:cover; }
.avatar-fallback { width:36px; height:36px; display:flex; align-items:center; justify-content:center; font-size:14px; }
.header-right { display:flex; align-items:center; gap:12px; }
.user-info { display:flex; flex-direction:column; line-height:1; }
.user-name { font-weight:600; font-size:14px; color:var(--fg); }
.user-email { font-size:12px; color:var(--muted); }
.dropdown-menu { position:absolute; right:0; top:56px; min-width:180px; background:var(--panel); border:1px solid var(--border); border-radius:10px; box-shadow:0 8px 30px rgba(2,6,23,0.6); padding:8px; z-index:60; }
.dropdown-item { display:block; padding:8px 10px; border-radius:8px; color:var(--fg); text-decoration:none; }
.dropdown-item:hover { background:rgba(255,255,255,0.02); }
.dropdown-divider { height:1px; background:var(--border); margin:6px 0; border-radius:2px; }
